mapper.select:
SELECT 
	writer_no, target_no, content, regdate, moddate
FROM memo
WHERE writer_no = #{writerNo}
	AND target_no = #{targetNo}

mapper.update:
UPDATE memo
SET
	content = #{content},
	moddate = SYSDATE
WHERE writer_no = #{writerNo}
	AND target_no = #{targetNo}

mapper.delete:
DELETE FROM memo
WHERE writer_no = #{writerNo}
	AND target_no = #{targetNo}
